mapper.update:
UPDATE scustomer
SET password = #{password}, name = #{name}, email = #{email}, division =
#{division}
, idno = #{idno}, address = #{address}
WHERE custid = #{custid}

mapper.delete:
DELETE
FROM scustomer
WHERE custid = #{custid} AND password = #{password}

mapper.select:
SELECT *
FROM scustomer
WHERE custid = #{custid} AND password = #{password}

SELECT custid
FROM scustomer
WHERE custid = #{custid}
